sys_script_client.script:
function onLoad() {
	try {
		var ga = new GlideAjax('ajaxUtils');
		ga.addParam('sysparm_name', 'getEnvironments');
		ga.getXML(parseMessage);
	}
	catch(err){
		jslog('An error occured: ' + err);
	}
}

function parseMessage(response) {

	try {
		var data = JSON.parse(response.responseXML.documentElement.getAttribute('answer')).answer;

		g_form.clearOptions('environment');
		data.forEach(function (item) {
			g_form.addOption('environment', item.number, item.name);
		});
	}
	catch(err) {
		jslog('An error occured 2: ' + err);
	}
}
