var ajaxUtils = Class.create();
ajaxUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getEnvironments: function() {
		var gr = new GlideRecord('x_209235_f5_as3_environment');
		gr.query();

		var envList = [];
		try {
			while (gr.next()) {
				envList.push({name: gr.getValue('name'), number: gr.getValue('number')});
			}
			//jsonData = new global.JSON().encodeArray(envList);
			//gs.info(jsonData);
		}
		catch (err) {
			gs.info("ERROR OCCURED " + err);
		}
		var response = { answer: envList };

		//var jsonData = new JSON();
		//return jsonData.encode(response);
		return JSON.stringify(response);
	},
    type: 'ajaxUtils'
});
