sys_script_include.script:
var f5AppHelper = Class.create();
f5AppHelper.prototype = {
    initialize: function() {
		this.clusterTable = 'x_209235_f5_as3_bigipcluster';
		this.environmentTable = 'x_209235_f5_as3_environment';
		this.applicationTable = 'x_209235_f5_as3_applications';
	},
	getApps: function() { 		
		var gr = new GlideRecord(this.applicationTable);
		gr.query();

		var appList = [];
		while (gr.next()) {
			appList.push({
				name: gr.getValue('name'),
				number: gr.getValue('number'),
				as3_definition: gr.getValue('as3_definition')
			});
		}

		return appList;
	},
	getClusters: function() { 	
		var gr = new GlideRecord(this.clusterTable);
		gr.query();

		var clusterList = [];
		while (gr.next()) {
			clusterList.push({name: gr.getValue('name'), number: gr.getValue('number')});
		}
		return clusterList;
	},
	getEnvironments: function() {
		var gr = new GlideRecord(this.environmentTable);
		gr.query();

		var envList = [];
		while (gr.next()) {
			envList.push({name: gr.getValue('name'), number: gr.getValue('number')});
		}
		return envList;
	},
    type: 'f5AppHelper'
};
