sys_ui_script.script:
var app = angular.module('as3App', []);

// Define Controller for managing AS3 Applications
app.controller('AppDefCtrl', ['$scope', function($scope) {
	$scope.applications = getApplications(); 
	$scope.myVar = 'blah';

	//$scope.getApplications = function() {
	function getApplications() {
        var ga = new GlideAjax('ajaxUtils');
		ga.addParam('sysparm_name', 'getApplications');
		ga.getXML(function(response){
			if (!response) 
				return;

			data = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));
			$scope.applications = data;
			console.log(data);
		});
	//};
	}
}]);
