sysauto_script.script:
var f5App = new f5AppHelper();
//var as3 = new AS3ApiUtils();

var gr = new GlideRecord('x_209235_f5_as3_integrationscripts');
gr.addQuery('name', 'test');
gr.query();

var gr2 = new GlideRecord('x_209235_f5_as3_applications');
gr2.addQuery('number', 'APP0001066');
gr2.query();
gr2.next();

var operation = "insert";
var appData = JSON.parse(gr2.getValue('app_data'));

if(gr.next()) {	
	var evaluator = new GlideScopedEvaluator();
	evaluator.putVariable('scriptName', gr.getValue('name'));
	evaluator.putVariable('operation', operation);
	evaluator.putVariable('appData', appData);
	evaluator.putVariable('result', null);
	var result = evaluator.evaluateScript(gr, 'script', null);
	gs.info("THIS IS THE RESULT: " + evaluator.getVariable('result'));
}
